import React, { useState, useEffect } from 'react';
import './App.css';
import { Header } from './components/Header/header';
import { Search } from './components/Search/search';
import { FilmList } from './components/List/film-list';

function App() {
  const [search, saveSearch] = useState({
    name: ''
  });

  const [nameList, saveNameList] = useState({});

  const [isLoaded, setIsLoaded] = useState(false);

  const fetchData = async searchInput => {
    const response = await fetch(
      `https://swapi.co/api/people/?search=${searchInput}`
    );

    if (response.status === 200) {
      return response.json();
    }
    // Handle error here
  };

  const handleData = async input => {
    if (!isLoaded) {
      try {
        const fetchedData = await fetchData(input);
        saveNameList(fetchedData);
        setIsLoaded(true);
        console.log(nameList.results[0].name);
      } catch (err) {
        throw 'ERROR LOADING';
      }
    }
  };

  return (
    <div className="text-center container">
      <Header />
      <Search
        fetchData={fetchData}
        handleData={handleData}
        userInput={userInput}
      />
      <FilmList nameList={nameList} />
    </div>
  );
}

export default App;
