export { ENDPOINT, CHARACTERS, getFilms, getCharacters, allCharacters };

const ENDPOINT = 'https://swapi.co/api/films';
const CHARACTERS = [
  'https://swapi.co/api/people/?page=1',
  'https://swapi.co/api/people/?page=2',
  'https://swapi.co/api/people/?page=3',
  'https://swapi.co/api/people/?page=4',
  'https://swapi.co/api/people/?page=5',
  'https://swapi.co/api/people/?page=6',
  'https://swapi.co/api/people/?page=7',
  'https://swapi.co/api/people/?page=8',
  'https://swapi.co/api/people/?page=9'
];
let allCharacters = [];

function checkStatus(response) {
  response.status === 200
    ? console.log('Successful request!')
    : console.error(`Oops, we get a ${response.status} error`);
  return response.json();
}

function getFilms(url) {
  return fetch(url)
    .then(checkStatus)
    .then(json => console.log(json.results));
}

function getCharacters(url) {
  Promise.all(
    CHARACTERS.map(
      CHARACTERS =>
        fetch(CHARACTERS)
          .then(checkStatus)
          .then(json => {
            console.log(json);
            allCharacters.push(json.results);
            return allCharacters;
          })
      // .then(data => console.log(data))
    )
  );
}
