export { FILMS, CHARACTERS, getFilms, getCharacters };

const FILMS = 'https://swapi.co/api/films';
let num = 1;
const CHARACTERS = 'https://swapi.co/api/people/' + num;
let allCharacters = [];

function checkStatus(response) {
  response.status === 200
    ? console.log('Successful request!')
    : console.error(`Oops, we get a ${response.status} error`);
  return response.json();
}

function getFilms(url) {
  return fetch(url)
    .then(checkStatus)
    .then(json => console.log(json));
}

function getCharacters(url) {
  while (num < 88) {
    return fetch(url + num)
      .then(checkStatus)
      .then(json => {
        allCharacters.push(json.name);
        console.log(allCharacters);
      });
  }
}
