import React, { useState } from 'react';
import requestAPI from '../../App';

export const Search = ({ search, saveSearch, userInput }) => {
  const [error, saveError] = useState(false);

  const { name } = search;
  const requestAPI = async () => {
    if (input) {
      const url = `https://swapi.co/api/people/?search=${name}`;
      const res = await fetch(url);
      const response = await res.json();
      const result = await response.results[0];
      saveNameList(result);
    }
  };

  const handleChange = e => {
    saveSearch({
      ...search,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (name.trim() === '') {
      saveError(true);
      return;
    } else {
      saveError(false);
      userInput(true);
    }
  };

  return (
    <div className="h-64 text-center items-center">
      <form action="" onSubmit={handleSubmit}>
        {error ? (
          <p className="text-yellow-500 font-bold">El campo es obligatorio</p>
        ) : null}
        <div>
          <label className="inline-block align-middle ">
            <input
              type="text"
              name="name"
              value={name}
              onChange={handleChange}
            ></input>
          </label>
        </div>

        <div>
          <button
            type="submit"
            value="Seach character"
            className="text-yellow-500 font-bold"
            onClick={requestAPI}
          >
            Search
          </button>
        </div>
      </form>
    </div>
  );
};
