import React, { useState } from 'react';

export const Search = () => {
  const [search, saveSearch] = useState({
    name: ''
  });

  const [error, saveError] = useState(false);

  const { name } = search;

  const handleChange = e => {
    saveSearch({
      ...search,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = e => {
    e.preventDefault();

    if (name.trim() === '') {
      saveError(true);
      return;
    }
    saveError(false);
  };

  return (
    <div className="h-64 text-center items-center">
      <form action="" onSubmit={handleSubmit}>
        {error ? (
          <p className="text-yellow-500 font-bold">El campo es obligatorio</p>
        ) : null}
        <div>
          <label className="inline-block align-middle ">
            <input
              type="text"
              name="name"
              value={name}
              onChange={handleChange}
            ></input>
          </label>
        </div>
        <div>
          <button type="submit" className="text-yellow-500 font-bold">
            Search
          </button>
        </div>
      </form>
    </div>
  );
};
