import React, { useState } from 'react';

export const Search = ({ search, saveSearch, userInput }) => {
  const { name } = search;

  const [isLoaded, setIsLoaded] = useState(false);

  const fetchData = async searchInput => {
    const response = await fetch(
      `https://swapi.co/api/people/?search=${searchInput}`
    );

    if (response.status === 200) {
      return response.json();
    }
    // Handle error here
  };

  const handleData = async input => {
    if (!isLoaded) {
      try {
        const fetchedData = await fetchData(input);
        saveNameList(fetchedData);
        setIsLoaded(true);
        console.log(nameList.results[0].name);
      } catch (err) {
        throw 'ERROR LOADING';
      }
    }
  };

  const handleChange = e => {
    saveSearch({
      ...search,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (name.trim() === '') {
      saveError(true);
      return;
    } else {
      saveError(false);
      userInput(true);
    }
  };

  return (
    <div className="h-64 text-center items-center">
      <form action="" onSubmit={handleSubmit}>
        {error ? (
          <p className="text-yellow-500 font-bold">El campo es obligatorio</p>
        ) : null}
        <div>
          <label className="inline-block align-middle ">
            <input
              type="text"
              name="name"
              value={name}
              onChange={handleChange}
            ></input>
          </label>
        </div>

        <div>
          <button
            type="submit"
            value="Seach character"
            className="text-yellow-500 font-bold"
          >
            Search
          </button>
        </div>
      </form>
    </div>
  );
};
